import { Link, useNavigate } from 'react-router-dom';
import { useState } from 'react';

function Register({ onSubmit }) {
  const [formValue, setFormValue] = useState({
    email: '',
    password: ''
  });
  //const [errorMessage, setErrorMessage] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormValue({
      ...formValue,
      [name]: value
    });
  }

  const handleSubmit = ({ email, password }) => {
    onSubmit({ email, password });
  }

  return (
    <section className="auth">
      <h2 className="auth__title">Регистрация</h2>
      <form className="auth__form popup__form">
        <input
          class="auth__input"
          type="emil"
          name="email"
          id="email"
          placeholder="Email"
          onChange={handleChange}
          required
        />
        <span class="error" id="fullnameInput-error"></span>
        <input
          class="auth__input"
          type="password"
          name="password"
          id="password"
          placeholder="Пароль"
          onChange={handleChange}
          required
        />
        <button className="auth__btn" onClick={handleSubmit}>Зарегистрироваться</button>
      </form>
      <p className="auth__link">Уже зарегестрированны? <>Войти</></p>
    </section>
  )
}

export default Register;
