import profileAvatarBtnEdit from '../images/Edit.svg';
import profileAvatar from '../images/image.jpg';
import profileBtnEdit from '../images/EditButton.svg';
import btnAddCard from '../images/AddButton.svg';
import btnDeleteCard from '../images/Trash.svg';
import btnLikeCard from '../images/Vector.svg';

const popupAvatar = document.querySelector('#popupEditAvatar');
const popupIdentity = document.querySelector('#popup-profile');
const popupAddCard = document.querySelector('#popupAdd');

const editAvatarBtn = document.querySelector('.profile__avatar-btnedit');
const editIdentity = document.querySelector('.profile__info-btn');
const buttonAdd = document.querySelector('.profile__button')

function Main() {
  function handleEditAvatarClick() {
    popupAvatar.classList.add('.popup_opened');
  }
  function handleEditProfileClick() {
    popupIdentity.classList.add('.popup_opened');
  }

  function handleAddPlaceClick() {
    popupAddCard.classList.add('.popup_opened');
  }


  return (
    <main className="content">
      <section className="profile">
        <div className="profile__avatar">
          <button className="profile__avatar-btnedit" type="button">
            <img className="profile__avatar-edit" src={profileAvatarBtnEdit} alt="Кнопка редактирования" />
          </button>
          <img className="profile__avatarimg" src={profileAvatar} alt="Аватар" />
        </div>
        <div className="profile__info">
          <div className="profile__info-title">
            <h1 className="profile__info-name">Жак-Ив Кусто</h1>
            <button className="profile__info-btnborder" type="button">
              <img className="profile__info-btn" src={profileBtnEdit} alt="Кнопка редактирования" />
            </button>
          </div>
          <p className="profile__info-identity">Исследователь океана</p>
        </div>
        <button className="profile__button" type="button">
          <img className="profile__button-plus" src={btnAddCard} alt="Кнопка добавления" />
        </button>
      </section>
      <section className="cards" id="cards">
        <template id="cardTemplate">
          <article className="card">
            <button className="card__delete" type="button">
              <img className="card__trash" src={btnDeleteCard} alt="Кнопка удаления" />
            </button>
            <img className="card__image" src="#" alt="#" />
            <div className="card__title">
              <h2 className="card__name"></h2>
              <div className="card__like-container">
                <button className="card__like" type="button">
                  <img src={btnLikeCard} alt="Кнопка лайк" />
                </button>
                <p className="card__like-number">0</p>
              </div>
            </div>
          </article>
        </template>
      </section>
    </main>
  )
}
editAvatarBtn.addEventListener("click", handleEditAvatarClick());
editIdentity.addEventListener("click", handleEditProfileClick());
buttonAdd.addEventListener("click", handleAddPlaceClick());

export default Main;
